
public class DoWhileRunOnce {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// In this example, do while loop will run once then tests the condition, 
		// then exits the loop since condition is false
		
		
		int count = 10;
		
		do {
			System.out.println("Count: "+ count);
			count++;
			
		}while(count <= 5);
		
		

	}

}
