package day06_operators2;

public class OperatorsPrecedence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//If + is between string and number, it will act as concatenation
		System.out.println("Hello "+6+10);
		
		//if + is between 2 numbers it will add as an arithmetic operator
		System.out.println(6+10 + "Hello ");
		
		//5/2 = 2
		//5.0 / 2 = 2.5
		//5 % 2 = 1
		
		
		
	}

}
