package day06_operators2;

public class ShortHandOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int students = 45;	
	
	System.out.println("students: "+ students);
		students = students + 5;
		
		System.out.println("students: "+ students);
		
		students = students - 2;
		
		System.out.println("students: "+ students);
		System.out.println();
		
		int teachers = 10;
		
		System.out.println("teachers: "+ teachers);
		
		teachers+=2;  //this means increase teachers by 2
		// or teachers = teachers + 2
		
		System.out.println("teachers: "+ teachers);
		
		teachers -=5; //decrease teachers by 5
		//teachers = teachers - 5
		
		System.out.println("teachers: "+ teachers);
		System.out.println();
		
		int cars = 12;
		System.out.println("CarSelector: "+ cars);
		
		cars *= 2; //cars = cars * 2
		
		cars += cars; // cars + cars i.e. double cars
		
		System.out.println("CarSelector: "+ cars);
		System.out.println();
		
		int shoes = 20;
		
		shoes /=4; // shoes / 4
		
		System.out.println("Shoes: "+ shoes);
		
		double price = 45.50;
		
		int amount = 5;
		
		price +=amount;
		
		System.out.println(price);
		
		
		
				
		
		
		
		
	}

}
