package day08_ConditionalStatement;

public class ConditionalIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		int x = a;
		
		b = x;
		a = b;
		System.out.println(a +"," + b);  // result 10,10
		
		double a2 = 10.0;
		
		int b2 = (int)a; //put (int) before a to get result otherwise error
		
		a2 = 20.0;
		
		System.out.println(a2 + "," +b2);
		
		System.out.println();
		System.out.println('a' + 1);  // refer to ASCII character chart table
		
		
		//
		
		
		
		
	}

}
